import React,{useState} from "react";
const NewListing = () =>{

    const [pName, setPName] = useState('');

    const handleProductAdd = (e) =>{
        e.prevent.default();
    }



    return(
        <div className='flex justify-center mt-10'>
            <div class="w-full max-w-lg">
                <form onSubmit={handleProductAdd} className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
                    <div className='mb-4 text-center text-lg font-bold text-gray-600'>
                        <h2>Add a new product to sell.</h2>
                    </div>
                    <div className="mb-4">
                        <label className="block text-sm font-bold tracking-tight text-gray-500 mb-2" for="email">
                            Product Name
                        </label>
                        <input 
                            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" 
                            id="email" 
                            type="email" 
                            placeholder="name@example.com"
                            value = {pName}
                            onChange={(e)=>setPName(e.target.value)}
                        />
                        <p className="text-red-500 text-xs italic"></p>
                    </div>
                    <div className="flex items-center justify-center">
                        <button 
                            className=" w-full bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline" 
                            type="submit"

                        >
                            Post
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
}

export default NewListing;
