import React,{useState,  useEffect} from "react";
import Navbar from "../components/Navbar";
import { useNavigate } from "react-router-dom";
import Product from '../components/Productcard';
const Home = ({token , setToken }) =>{

    const navigate = useNavigate();

    const [loading, setLoading] = useState(false);
    const [listings, setListings] = useState([]);

    

    const fetchListings = async () =>{
        setLoading(true)
        try{
            const response = await fetch('http://localhost:5000/api/listings')
            const tours = await response.json()
            setLoading(false)
            setListings(tours)
        } catch(error){
            setLoading(false)
            console.log("Error Occured");
            console.log(error);
        }
    }
    useEffect(() => {
        if(!token){
            return (navigate('/login'));
        }
        fetchListings()
    }, [token])

    

    if(loading){
        return (
            <div className="grid justify-items-center items-center h-screen">
                <div>
                    <h3 className="text-center text-xl">Loading..</h3>
                    <p className="text-sm">this might take a few minutes</p>
                </div>
            </div>
        );
    }
    return(
        <>
        <Navbar setToken={setToken} />
        <div class="grid 2xl:grid-cols-5 xl:grid-cols-4 lg:grid-cols-3 sm:grid-cols-2 grid-cols-1 gap-x-6 gap-y-12 w-full mt-6 md:px-15 px-5">
            {
                listings.map((data)=>{
                    console.log(data);
                    return <>
                        <div>
                            <a href="#" className="block h-64 rounded-lg shadow-lg bg-white px-2">
                                <img src={`http://localhost:5000/${data.prodcutImg}`} className="rounded object-scale-down" />
                                <br />
                                <p className="text-xs">{data.description}</p>
                            </a>
                            <div className="flex items-center justify-between mt-3">
                            <div>
                                <a href="#" className="font-medium">{data.title}</a>
                                <a className="flex items-center" href="#">
                                <span className="text-xs font-medium text-gray-600">by</span>
                                <span className="text-xs font-medium ml-1 text-indigo-500">{data.name}</span>
                                </a>
                            </div>
                            <span className="flex items-center h-8 bg-indigo-200 text-indigo-600 text-sm px-2 rounded">${data.expectedPrice}</span>
                            </div>
                        </div>
                    </>
                })
            }
        </div>
        </>
    );
}

export default Home;
