import React,{useState} from 'react';

const Login = () => {

    const [status, setStatus] = useState(0);
    const [token, setToken] = useState("");
    const [name,  setName] =  useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const LoginStatusMessage = (props) =>{
        if(props.status===200){
            return <div className='bg-green-300 text-xs rounded px-1 py-1'>
                Hi, {props.name}
            </div>
        }
        if(props.status===400){
            return <div className='bg-red-300 text-xs rounded px-1 py-1'>
                Wrong Password !
            </div>
        }
        else{
            return <></>
        }
    }

    const handleLogin = (event) =>{
        event.preventDefault();
        if(email && password){
            console.log(password,email);
            fetch('http://localhost:5000/api/user/login',{
                method: 'POST',
                headers: {
                'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    email:email,
                    password:password,
                })
            })
            .then(function (response) {
                setStatus(response.status)
                return response.json();
            })
            //Then with the data from the response in JSON...
            .then((data) => {
                console.log(data.message)
                setToken(data.token);
                setName(data.name);
                return console.log('Success:', data);
            })
            //Then with the error genereted...
            .catch((error) => {
            console.error('Error:', error);
            });

        }else{
            console.log("Something wrong !!")
        }
    }

    return(
        <div className='flex justify-center mt-10'>
            <div class="w-full max-w-xs">
                <form onSubmit={handleLogin} className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
                    <div className='mb-4 text-center text-lg font-bold text-gray-600'>
                        <h2>Sign In</h2>
                    </div>
                    <div className='mb-4'>
                        <LoginStatusMessage status={status} name={name} token={token}/>
                    </div>
                    <div className="mb-4">
                        <label className="block text-sm font-bold tracking-tight text-gray-500 mb-2" for="email">
                            Email
                        </label>
                        <input 
                            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" 
                            id="email" 
                            type="email" 
                            placeholder="name@example.com"
                            value = {email}
                            onChange={(e)=>setEmail(e.target.value)}
                        />
                        <p className="text-red-500 text-xs italic"></p>
                    </div>
                    <div className="mb-6">
                        <label className="block text-sm font-bold tracking-tight text-gray-500 mb-2" for="password">
                            Password
                        </label>
                        <input 
                            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 mb-3 leading-tight focus:outline-none focus:shadow-outline" 
                            id="password" 
                            type="password" 
                            placeholder="******************"
                            value={password}
                            onChange={(e)=>{setPassword(e.target.value)}}
                        />
                        <p className="text-red-500 text-xs italic"></p>
                    </div>
                    <div className="flex items-center justify-center">
                        <button 
                            className=" w-full bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline" 
                            type="submit"

                        >
                            Sign In
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
}

export default Login;
