import React from "react";
import useToken from './useToken'
import { NavLink } from "react-router-dom";
const Navbar = () =>{

    const {token, setToken, user} = useToken();

    return(
        <nav className='flex justify-around shadow-sm px-2 py-4'>
            <div>
            LOGO
            </div>
            <div className='flex gap-4'>
                {token?<button className='px-2 py-1 font-semibold text-sm bg-red-500 text-white rounded shadow-sm' onClick={()=>{setToken('')}}>Logout</button>:<NavLink 
                    exact 
                    className="text-purple-900 dark:text-gray-100 hover:text-purple-400 dark:hover:text-purple-400 dark:text-slate-300 focus:text-purple-500  dark:focus:text-purple-500" 
                    activeClassName="text-purple-500"  
                    to="/login">
                Sign In
                </NavLink>}
            </div>
        </nav>
    );
}

export default Navbar;
