import React,{useState} from 'react';
import './App.css';
import Login from './pages/Login'
import HomePage from './pages/Home'
import useToken from './components/useToken'
import {Buffer} from 'buffer';

function App() {

  const {token, setToken} = useToken();

  const str = token.split('.')[1];
  const buff = new Buffer(str, 'base64');
  const base64ToStringNew = buff.toString('ascii');
  const userdetails = JSON.parse(base64ToStringNew);
  setUser(userdetails);

    return(
    <div className='container px-2'>
      {token?
      <>Hi<button onClick={()=>{setToken('')}}>Logout</button></>:<Login setToken={setToken}/>}
    </div>
  );

}

export default App;
