import React,{useState} from "react";
const NewListing = () =>{

    const [pName, setPName] = useState('');
    const [productImage, setProductImage] = useState('');
    const [category, setCategory] = useState('');
    const [description, setDescription] = useState('');
    const [expPrice, setExpPrice] = useState('');
    const [age, setAge] = useState('');


    const handleProductAdd = (e) =>{
        e.prevent.default();
    }

    const handleProductImageChange = (e) =>{
        console.log(e.target.value);
        // fetch('https://api.imgbb.com/1/upload',{
        //         method: 'POST',
        //         body: JSON.stringify({
        //             key:'b539bab6cd8968bc2efb62735dfc2181',
        //             image:e.target.value,
        //         })
        //     })
        //     .then(function (response) {
        //         console.log(response.status)
        //         return console.log(response.json());
        //     })
        //     .catch((error) => {
        //     console.error('Error:', error);
        //     });
    }


    return(
        <div className='flex justify-center mt-10'>

            <div class="w-full max-w-xl">

                <form onSubmit={handleProductAdd} className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">

                    <div className='mb-4 text-center text-lg font-bold text-gray-600'>
                        <h2>Add a new product to sell.</h2>
                    </div>


                    <div className="mb-4">
                        <label className="block text-sm font-bold tracking-tight text-gray-500 mb-2" for="email">
                            Product Name
                        </label>
                        <input 
                            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" 
                            id="email" 
                            type="email" 
                            placeholder="Product Name"
                            value = {pName}
                            onChange={(e)=>setPName(e.target.value)}
                        />
                        <p className="text-red-500 text-xs italic"></p>
                    </div>

                    <div className="mb-4">
                        <label className="block text-sm font-bold tracking-tight text-gray-500 mb-2" for="category">
                            Product Category
                        </label>
                        <select  onChange={(e)=>{setCategory(e.target.value)}} name="category" id="category" className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline">
                            <option disabled value={category}> -- select an option -- </option>
                            <option value="mobile">Mobile</option>
                            <option value="laptop">Laptop</option>
                            <option value="Books">Book</option>
                        </select>
                        <p className="text-red-500 text-xs italic"></p>
                    </div>

                    <div className="mb-4">
                        <label className="block text-sm font-bold tracking-tight text-gray-500 mb-2" for="description">
                            Description
                        </label>
                        <textarea onChange={(e)=>{setDescription(e.target.value)}} className=" shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline">{description}</textarea>
                        <p className="text-red-500 text-xs italic"></p>
                    </div>

                    <div className="mb-4">
                        <label className="block text-sm font-bold tracking-tight text-gray-500 mb-2" for="expPrice">
                            Expected Price 
                        </label>
                        <input 
                            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" 
                            id="expPrice" 
                            type="number" 
                            placeholder="Rs1000"
                            value = {expPrice}
                            onChange={(e)=>setExpPrice(e.target.value)}
                        />
                        <p className="text-red-500 text-xs italic"></p>
                    </div>

                    <div className="mb-4">
                        <label className="block text-sm font-bold tracking-tight text-gray-500 mb-2" for="age">
                            How old is your product? 
                        </label>
                        <input 
                            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" 
                            id="age" 
                            type="number" 
                            placeholder="10 yrs old"
                            value = {age}
                            onChange={(e)=>setAge(e.target.value)}
                        />
                        <p className="text-red-500 text-xs italic"></p>
                    </div>


                    <br />
                    
                    <div className="max-w-2xl rounded-lg">
                        <div className="m-4">
                            <label className="inline-block mb-2 text-gray-500">File Upload</label>
                            <div className="flex items-center justify-center w-full">
                            <label className="flex flex-col w-full h-32 border-4 border-blue-200 border-dashed hover:bg-gray-100 hover:border-gray-300">
                                <div className="flex flex-col items-center justify-center pt-7">
                                <svg xmlns="http://www.w3.org/2000/svg" className="w-8 h-8 text-gray-400 group-hover:text-gray-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 16a4 4 0 01-.88-7.903A5 5 0 1115.9 6L16 6a5 5 0 011 9.9M15 13l-3-3m0 0l-3 3m3-3v12" />
                                </svg>
                                <img src={productImage} />
                                <p className="pt-1 text-sm tracking-wider text-gray-400 group-hover:text-gray-600">
                                    Attach a file</p>
                                </div>
                                <input type="file" className="opacity-0" 
                                value = {productImage}
                                onChange={handleProductImageChange} 
                                />
                            </label>
                            </div>
                        </div>
                        <div className="flex justify-center p-2">
                            <button className="w-sm px-4 py-2 text-white bg-blue-500 rounded">Upload</button>
                        </div>
                        </div>
                    

                    <div className="flex items-center justify-center">
                        <button 
                            className=" w-full bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline" 
                            type="submit"
                        >
                            Post
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
}

export default NewListing;
