import React,{useState,  useEffect} from "react";
import Navbar from "../components/Navbar";
import { useNavigate } from "react-router-dom";
import Product from '../components/Productcard';
const Home = ({token , setToken }) =>{

    const navigate = useNavigate();

    const [loading, setLoading] = useState(false);
    const [listings, setListings] = useState([]);

    

    const fetchListings = async () =>{
        setLoading(true)
        try{
            const response = await fetch('http://localhost:5000/api/listings')
            const tours = await response.json()
            setLoading(false)
            setListings(tours)
        } catch(error){
            setLoading(false)
            console.log("Error Occured");
            console.log(error);
        }
    }
    useEffect(() => {
        if(!token){
            return (navigate('/login'));
        }
        fetchListings()
    }, [token])

    

    if(loading){
        return (
            <div className="grid justify-items-center items-center h-screen">
                <div>
                    <h3 className="text-center text-xl">Loading..</h3>
                    <p className="text-sm">this might take a few minutes</p>
                </div>
            </div>
        );
    }
    return(
        <>
        <Navbar setToken={setToken} />
        <div class="flex flex-nowrap justify-center items-center px-2 py-2">
            {
                listings.map((data)=>{
                    console.log(data);
                    return <>
                    {data.productImg}
                    {data.name}<br />
                    </>
                })
            }
        </div>
        </>
    );
}

export default Home;
